package org.eclipse.core.tests.internal.indexing;import org.eclipse.core.internal.indexing.*;public class TestObjectPolicy extends AbstractObjectPolicy {	/**	 * Constructor for TestObjectPolicy	 */	public TestObjectPolicy() {		super();	}	/**	 * @see ObjectPolicy#createObject(Field, ObjectStore, ObjectAddress)	 */	public StoredObject createObject(Field field, ObjectStore store, ObjectAddress address) throws ObjectStoreException {		return new TestObject(field, store, address);		}}